import React, { useContext, useEffect } from "react";
import { ProductContext } from "../contexts/ProductContext";
import { CategorySelectorContext } from "../contexts/CategorySelectorContext";
import { ProductCard } from "../components/ProductCard";

const ProductList = () => {
  const { bookList, setBookList } = useContext(ProductContext);
  const {
    isFiction,
    isNonFiction,
    isHorror,
    setFiction,
    setNonFiction,
    setHorror,
  } = useContext(CategorySelectorContext);

  const handleCategorySelection = (event) => {
    const name = event.target.name;
    if (name === "fiction") {
      setFiction(!isFiction);
    }
    if (name === "nonFiction") {
      setNonFiction(!isNonFiction);
    }
    if (name === "horror") {
      setHorror(!isHorror);
    }
  };

  useEffect(() => {
    let filteredBooks = [...bookList];

    if (isFiction) {
      filteredBooks = filteredBooks.filter((book) => book.categoryName === "fiction");
    }
    if (isHorror) {
      filteredBooks = filteredBooks.filter((book) => book.categoryName === "horror");
    }
    if (isNonFiction) {
      filteredBooks = filteredBooks.filter((book) => book.categoryName === "nonFiction");
    }

    setBookList(filteredBooks);
  }, [isFiction, isHorror, isNonFiction, setBookList]);

  return (
    <div>
      <aside>
        {/* category selection filter */}
        <div>
          <label>
            Fiction
            <input
              type="checkbox"
              checked={isFiction}
              onChange={handleCategorySelection}
              name="fiction"
            />
          </label>
          <label>
            Non Fiction
            <input
              type="checkbox"
              checked={isNonFiction}
              onChange={handleCategorySelection}
              name="nonFiction"
            />
          </label>
          <label>
            Horror
            <input
              type="checkbox"
              checked={isHorror}
              onChange={handleCategorySelection}
              name="horror"
            />
          </label>
        </div>
      </aside>
      {bookList.map((book) => (
        <ProductCard book={book} key={book.id} />
      ))}
    </div>
  );
};

export { ProductList };
