import { createContext, useState } from "react";

const CategorySelectorContext = createContext();

const CategorySelectorProvider = ({ children }) => {
  const [isFiction, setFiction] = useState(false);
  const [isNonFiction, setNonFiction] = useState(false);
  const [isHorror, setHorror] = useState(false);
  return (
    <CategorySelectorContext.Provider
      value={{
        isFiction,
        isNonFiction,
        isHorror,
        setFiction,
        setNonFiction,
        setHorror,
      }}
    >
      {children}
    </CategorySelectorContext.Provider>
  );
};

export { CategorySelectorContext, CategorySelectorProvider };
