import { ProductCard } from "../components/ProductCard";
const ProductList = () => {
  const { bookList } = useContext(ProductContext);
  const {
    isFiction,
    isNonFiction,
    isHorror,
    setFiction,
    setNonFiction,
    setHorror
  } = useContext(CategorySelectorContext);
  const handleCategorySelection = (event) => {};
  return (
    <div>
      <aside>
        <div>
          <label>
            Fiction
            <input
              type="checkbox"
              checked={isFiction}
              onChange={handleCategorySelection}
              name="fiction"
            />
          </label>
          <label>
            Non Fiction
            <input
              type="checkbox"
              checked={isNonFiction}
              onChange={handleCategorySelection}
              name="nonFiction"
            />
          </label>
          <label>
            Horror
            <input
              type="checkbox"
              checked={userCategorySelection.horror}
              onChange={handleCategorySelection}
              name="horror"
            />
          </label>
        </div>
      </aside>
      {bookList.map((book) => (
        <ProductCard book={book} />
      ))}
    </div>
  );
};
export { ProductList };
