import { useContext } from "react";

import { ProductContext } from "../contexts/ProductContext";
import { CategorySelectorContext } from "../contexts/CategorySelectorContext";
import { ProductCard } from "../components/ProductCard";
const ProductList = () => {
  const { bookList } = useContext(ProductContext);
  const { userCategorySelection, selectCategory, unselectCategory } =
    useContext(CategorySelectorContext);
  return (
    <div>
      <aside>
          {/* price filter */}
          {/* category filter */}
          <select>
              <option>Fiction</option>
              <option>Non fiction</option>
              <option>horror</option>
          </select>
      </aside>
      {bookList.map((book) => (
        <ProductCard book={book} />
      ))}
    </div>
  );
};
export { ProductList };
