import { useContext } from "react";

import { ProductContext } from "../contexts/ProductContext";
import { CategorySelectorContext } from "../contexts/CategorySelectorContext";
import { ProductCard } from "../components/ProductCard";
const ProductList = () => {
  const { bookList } = useContext(ProductContext);
  const { userCategorySelection, selectCategory, setUserCategorySelection} = useContext(
    CategorySelectorContext
  );
  const handleCategorySelection = (event) => {
      const name = event.target.name;
      setUserCategorySelection({...userCategorySelection, [name]: !userCategorySelection[name]});
  };
  return (
    <div>
      <aside>
        {/* price filter */}
        {/* category filter */}
        {/* <label>
          Category{" "}
          <select onChange={handleCategorySelection}>
            <option value="all categories">All categories</option>
            <option value="fiction">Fiction</option>
            <option value="nonFiction">Non fiction</option>
            <option value="horror">horror</option>
          </select>
        </label> */}
        <div>
          <label>
            Fiction
            <input
              type="checkbox"
              checked={userCategorySelection.fiction}
              onChange={handleCategorySelection}
              name="fiction"
            />
          </label>
          <label>
            Non Fiction
            <input
              type="checkbox"
              checked={userCategorySelection.nonFiction}
              onChange={handleCategorySelection}
              name="nonFiction"
            />
          </label>
          <label>
            Horror
            <input
              type="checkbox"
              checked={userCategorySelection.horror}
              onChange={handleCategorySelection}
              name="horror"
            />
          </label>
        </div>
      </aside>
      {bookList.map((book) => (
        <ProductCard book={book} />
      ))}
    </div>
  );
};
export { ProductList };
