import { useContext} from "react";

import { ProductContext } from "../contexts/ProductContext";
import { CategorySelectorContext } from "../contexts/CategorySelectorContext";
import { ProductCard } from "../components/ProductCard";

const ProductList = () => {
  const { bookList, setBookList } = useContext(ProductContext);
  const { categoryFilter, addToCategoryFilter, removeFromCategoryFilter } =
    useContext(CategorySelectorContext);




  return (
    <div>
      <aside>
        {/* category selection filter */}
        <div>
          <label>
            Fiction
            <input
              type="checkbox"
              checked={isFiction}
              onChange={handleCategorySelection}
              name="fiction"
            />
          </label>
          <label>
            Non Fiction
            <input
              type="checkbox"
              checked={isNonFiction}
              onChange={handleCategorySelection}
              name="nonFiction"
            />
          </label>
          <label>
            Horror
            <input
              type="checkbox"
              checked={isHorror}
              onChange={handleCategorySelection}
              name="horror"
            />
          </label>
        </div>
      </aside>
      {bookList.map((book) => (
        <ProductCard book={book} />
      ))}
    </div>
  );
};

export { ProductList };
